package constant

import "sort"

const defaultIcon = "people"

type IconInfo struct {
	Name   string `json:"name"` // icon名称
	System int    `json:"system"`
	Icon   string `json:"icon"` // icon的SVG文件的path元素内容
}

func AllIcons() []IconInfo {
	iconInfos := make([]IconInfo, 0, len(icons))
	for name, icon := range icons {
		info := IconInfo{Name: name, Icon: icon}
		if _, ok := systemIcon[name]; ok {
			info.System = 1
		}
		iconInfos = append(iconInfos, info)
	}
	sort.Slice(iconInfos, func(i, j int) bool {
		return iconInfos[i].Name < iconInfos[j].Name
	})
	return iconInfos
}

func ValidIcon(name string) bool {
	_, ok := icons[name]
	return ok
}

